// ================================ DECODE ================================
static inline void decodeMCP_KickerCharge(MCP_KickerCharge *mcpkc, MCP_KickerChargePayload *mcpkcp){
    mcpkc->ack_number    = MCP_KickerCharge_get_ack_number(mcpkcp);
    mcpkc->voltage       = MCP_KickerCharge_get_voltage(mcpkcp);
}

